import { useState } from 'react';

const Home = () => {
    //let name = 'Joel';
    const [name, setName] = useState('Joel');

    const handleClick = () => {
        setName('Sophie');
    }

    return (
        <div className="home">
           <h2>Homepage</h2>
           <p>{ name }</p> 
           <button onClick={handleClick}>Click me!</button>
        </div>
    );
}
 
export default Home;
