import React, { Component } from "react";


// car constructor
function car(name, speed, weight, acceleration, lane, isBet, bet, place) {
    this.name = name;
    this.speed = speed;
    this.weight = weight;
    this.acceleration = acceleration;
    this.lane = lane;
    this.isBet = isBet;
    this.bet = bet;
    this.place = place;

}
// cars
const car1 = new car(1, 100, 200, 5, 1, true, 50, 2);

const car2 = new car(2, 120, 300, 2, 2, false, 100, 1);

const car3 = new car(3, 90, 150, 7, 3, false, 200, 3);

let betCar;
let winnings;


class Bet extends Component {
    constructor() {
        super();
        this.state = {
            betPrice: 100,
            stakes: 4
        }
        this.handlePrice = this.handlePrice.bind(this)
        this.handleStake = this.handleStake.bind(this)
        this.clearStake = this.clearStake.bind(this)
        this.clearPrice = this.clearPrice.bind(this)
    }

    handleStake(){
        this.setState({
            stakes: this.state.stakes * 2
        })
    }

    clearStake(){
        this.setState({
            stakes: this.state.stakes * 0 + 4
        })
    }

    handlePrice(){
        this.setState({
            betPrice: this.state.betPrice + 10
        })
    }

    clearPrice(){
        this.setState({
            betPrice: this.state.betPrice * 0 + 50
        })
    }


    addCar1(){
        betCar = car1
        this.betMultiplier()
    }

    addCar2(){
        betCar = car2
        this.betMultiplier()
    }

    addCar3(){
        betCar = car3
        this.betMultiplier()
    }

    betMultiplier(){
        if (betCar.place === 1) {
            winnings = Bet.state.betPrice * 2;
            alert("First place!", winnings)
        } else if (betCar.place === 2) {
            winnings = Bet.state.betPrice * 1.25;
            alert("Second place!", winnings)
        } else if (betCar.place === 3) {
            winnings = Bet.state.betPrice * 0.5;
            alert("Third place!", winnings)
        }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>choose a car!</h1>
                    <button onClick={this.addCar1}>Car1</button>
                    <button onClick={this.addCar2}>Car2</button>
                    <button onClick={this.addCar3}>Car3</button>
                </div>
                <h1>{this.state.stakes}</h1>
                <h1>{this.state.betPrice}</h1>
                <div>
                    <button onClick={this.handleStake}>Increase Stakes</button>
                    <button onClick={this.clearStake}>Clear Stakes</button>
                    <button onClick={this.handlePrice}>Increase Price</button>
                    <button onClick={this.clearPrice}>Clear Price</button>
                </div>
            </div>
        );
    }
}

export default Bet;





