import React, { Component } from "react";


class Bet extends Component {
    constructor() {
        super();
        this.state = {
            betPrice: 100,
            stakes: 4
        }
        this.handlePrice = this.handlePrice.bind(this)
        this.handleStake = this.handleStake.bind(this)
        this.clearStake = this.clearStake.bind(this)
        this.clearPrice = this.clearPrice.bind(this)
    }

    handleStake(){
        this.setState({
            stakes: this.state.stakes * 2
        })
    }

    clearStake(){
        this.setState({
            stakes: this.state.stakes * 0 + 4
        })
    }

    handlePrice(){
        this.setState({
            betPrice: this.state.betPrice + 10
        })
    }

    clearPrice(){
        this.setState({
            betPrice: this.state.betPrice * 0
        })
    }

    render() {
        return (
            <div>
                <button>{this.state.stakes}</button>
                <button>{this.state.betPrice}</button>
                <div>
                    <button onClick={this.handleStake}>Increase Stakes</button>
                    <button onClick={this.clearStake}>Clear Stakes</button>
                    <button onClick={this.handlePrice}>Increase Price</button>
                    <button onClick={this.clearPrice}>Clear Price</button>
                </div>
                <div className="homebtn">

                </div> 
            </div>
        );
    }
}

export default Bet;

