import {
    Link
  } from "react-router-dom";

const Track = () => {
    return(
        <div>
            <h1>TRACK!</h1>
            <button onClick={startRace}>Start Race!</button>
            <Link to="/">
                <button>
                    Go back!
                </button>
            </Link>
        </div>
        
    )
}

function car(name, speed, weight, acceleration, lane, isBet) {
    this.name = name;
    this.speed = speed;
    this.weight = weight;
    this.acceleration = acceleration;
    this.lane = lane;
    this.isBet = isBet;
}

const car1 = new car("Car 1", 100, 200, 5, 1, true);

const car2 = new car("Car 2", 120, 300, 2, 2, false);

const car3 = new car("Car 3", 90, 150, 7, 3, false);

let raceCounter;

const startRace = () => {
    raceCounter = setInterval(carPosition, 1000);
}

let i = 0;
let lapCount = 0;

const carPosition = () => {
    if (i === 10) {
        lapCounter();
        i = 0;
    } else if (i < 10) {
        i++;
        console.log("Car Position =", i);
    }
}

const lapCounter = () => {
    if (i === 10) {
        if (lapCount === 3) {
            console.log("Race has ended!");
            clearInterval(raceCounter);
        } else if (lapCount < 3) {
            lapCount += 1;
            console.log("Lap:", lapCount);
        }
    }
}

