import React, { Component } from "react";


// car constructor
function car(name, speed, weight, acceleration, lane, isBet, bet, place) {
    this.name = name;
    this.speed = speed;
    this.weight = weight;
    this.acceleration = acceleration;
    this.lane = lane;
    this.isBet = isBet;
    this.bet = bet;
    this.place = place;

}
// cars
const car1 = new car(1, 100, 200, 5, 1, true, 50, 2);

const car2 = new car(2, 120, 300, 2, 2, false, 100, 1);

const car3 = new car(3, 90, 150, 7, 3, false, 200, 3);



class Bet extends Component {
    constructor() {
        super();
        this.state = {
            betPrice: 100,
            place: 3,
            bank: 500,
        }
        this.clearPrice = this.clearPrice.bind(this)
        this.betMultiplier = this.betMultiplier.bind(this)
    }



    handlePrice(){
        this.setState({
            betPrice: this.state.betPrice + 10
        })
    }

    clearPrice(){
        this.setState({
            betPrice: this.state.betPrice * 0 + 50
        })
    }

    betMultiplier(){
        if (this.state.place === 1) {
            this.setState({
                bank: this.state.bank + this.state.betPrice * 2
            })
        
        } else if (this.state.place === 2) {
            this.setState({
                bank: this.state.bank + this.state.betPrice * 1.25
            })
            
        } else if (this.state.place === 3) {
            this.setState({
                bank: this.state.bank - this.state.betPrice * 0.5
            })
           
        }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>bank account!</h1>
                    <h1>{this.state.bank}</h1>
                </div>
              
                <h1>{this.state.betPrice}</h1>
                <div>
                    <button onClick={this.handlePrice}>Increase Price</button>
                    <button onClick={this.clearPrice}>Clear Price</button>
                    <button onClick={this.betMultiplier}>BET!</button>
                </div>
            </div>
        );
    }
}

export default Bet;





