import {
    Link
  } from "react-router-dom";

const Track = () => {
    return(
        <div>
            <h1>TRACK!</h1>
            <Link to="/">
                <button>
                    Go back!
                </button>
            </Link>
        </div>
        
    )
}

let i = 0;
let timesRun = 0;

function lapCounter() {
    if (timesRun < 3){
        timesRun += 1;
        console.log("lap:", timesRun);
    } else {
        timesRun = 3;
        console.log("The race has ended!");
    }
}

let interval = setInterval(function(){
    if (i < 10) {
        i++;
        console.log("car position =", i);
    } if (i === 10) {
        i = 0;
        lapCounter();
    } if (timesRun === 3) {
        lapCounter();
        clearInterval(interval);
    }
}, 1000)


export default Track;
