import "./bank.css";
import bankTitle from "./grand turbo bank.png";
import bankVault from "./bank vault.png";
import Bet from "./Bet";
import React, { Component } from "react";
import {
    Link
  } from "react-router-dom";

// car constructor
class Car extends Component {
    constructor(name, speed, weight, acceleration, lane, isBet) {
        super();
        this.name = name;
        this.speed = speed;
        this.weight = weight;
        this.acceleration = acceleration;
        this.lane = lane;
        this.isBet = false;
        this.setBet = this.setBet.bind(this);
    }

    setBet() {
        this.isBet = false;
    }
}

// cars
const car1 = new Car("Car 1", 100, 200, 5, 1, false);

const car2 = new Car("Car 2", 120, 300, 2, 2, false);

const car3 = new Car("Car 3", 90, 150, 7, 3, false);



const Bank = () => {
    return(
        <div>
            {/* <div className="bankheader">
                <img src={bankTitle} alt="Grand Turbo Bank" id="bankTitle"/>
                <p>Welcome to <b>Grand Turbo Bank!</b> Keep all your coins in one of our state of the art high-security vaults! We will keep them safe, its a <b>Grand Turbo guarantee!</b></p>
                <img src={bankVault} alt="Grand Turbo Bank" id="vault"/>
            </div> */}
            <button onClick={car1.setBet}>Bet Car1</button>
            <button onClick={car2.setBet}>Bet Car2</button>
            <button onClick={car3.setBet}>Bet Car3</button>
            <Bet />
            <div className="homebtn">
                <Link to="/">
                    <button>
                        <a href="/">Go back!</a>
                    </button>
                </Link>
            </div>   
        </div>
    )
}

export default Bank;
